var dots=document.getElementsByClassName("dots");var more=document.getElementsByClassName("read-more");var btn=document.getElementsByClassName("btn-more-less");function readMoreLess(e){var clicked_card=e.currentTarget.getAttribute("data-button-more-less");btnText=btn[clicked_card];moreText=more[clicked_card];dots_card=dots[clicked_card];if(dots_card.style.display==="none"){dots_card.style.display="inline";btnText.innerHTML="Read more";moreText.style.display="none"}else{dots_card.style.display="none";btnText.innerHTML="Read less";moreText.style.display="inline"}}for(var i=0;i<btn.length;i+=1){btn[i].addEventListener("click",readMoreLess)}